#include<stdio.h>

void zimu(char *str)
{
    int all;
    for(i = 0; i < 20; i++)
    {
        all+=str[i];
    }
    switch(all%3)
    {
        case 0://直接从大到小排列
        case 1://去掉一个%3=1的最小数字，然后再从大到小排列
        //如果没有%3=1的数字，去掉两个%3=2的最小数字，然后再从大到小排列
        case 2://去掉一个%3=2的最小数字，然后再从大到小排列
        //如果没有%3=2的数字，去掉两个%3=1的最小数字，然后再从大到小排列
        
    }
}
int main()
{
    char str[20] = "52676345176278687845";
    zimu(str);
}